// tail recurrsion 
import scala.annotation.tailrec

def fibWithTailRec(n: BigInt): BigInt = {
    @tailrec
    def innerFib(curr: BigInt, prev: BigInt, next: BigInt): BigInt = {
      if (curr == 0) prev
      else innerFib(curr - 1, next, next+prev)
    }
    innerFib(n, 1, 1)
  }

  fibWithTailRec(10000)

